public class RotateStrikng_Word_wise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String str=" hi helo mi to ";
String []ch=str.split(" ");
for(int i=ch.length-1;i>=0;i--)
{
	System.out.print(ch[i]+" ");
}
System.out.println();
	}
}
